
CREATE TABLE IF NOT EXISTS vkuser
(
    id          SERIAL PRIMARY KEY,
    uid      	INTEGER,
    name     	VARCHAR(30),
    city        VARCHAR(50),
    timer       INTEGER
);

CREATE INDEX ON vkuser (uid);

CREATE TYPE weather_type AS ENUM ('fair', 'cloudy', 'rainy', 'windy');

CREATE TABLE IF NOT EXISTS film_weather
(
    id          SERIAL PRIMARY KEY,
    film      	VARCHAR(80),
    weather     weather_type
);

